abstract class ReadOnlyList<T> {
  T get first;
  int get length;
  T operator [](int index);
}

class ImmutableList<T> implements ReadOnlyList<T> {
  final List<T> _items;

  ImmutableList(List<T> items) : _items = List.unmodifiable(items);

  @override
  T get first => _items.first;

  @override
  int get length => _items.length;

  @override
  T operator [](int index) => _items[index];
}

class Statistics<T extends num> {
  final List<T> values;

  Statistics(this.values);

  T get min {
    var result = values.first;
    for (var v in values) {
      if (v < result) result = v;
    }
    return result;
  }

  T get max {
    var result = values.first;
    for (var v in values) {
      if (v > result) result = v;
    }
    return result;
  }

  double get average {
    var sum = 0.0;
    for (var v in values) {
      sum += v;
    }
    return sum / values.length;
  }
}

class SortedList<T extends Comparable<dynamic>> {
