  // Range with bounds
  print('');
  print('--- Range with Bounds ---');
  var intRange = Range<int>(10, 20);
  print('Range 10-20 contains 15: ${intRange.contains(15)}');
  print('Range 10-20 contains 25: ${intRange.contains(25)}');
