/// Demonstrates Dart arithmetic operators
///
/// Features covered:
/// - Addition, subtraction, multiplication, division
/// - Integer division, modulo
/// - Increment/decrement (pre and post)
/// - Unary minus

void main() {
  print('=== Arithmetic Operators ===\n');

  // Basic arithmetic
  print('--- Basic Arithmetic ---');
  int a = 10;
  int b = 3;

  print('a = $a, b = $b');
  print('a + b = ${a + b}'); // 13
  print('a - b = ${a - b}'); // 7
  print('a * b = ${a * b}'); // 30
  print('a / b = ${a / b}'); // 3.333... (double)
  print('a ~/ b = ${a ~/ b}'); // 3 (integer division)
  print('a % b = ${a % b}'); // 1 (modulo/remainder)

  // Division always returns double
  print('\n--- Division Types ---');
  print('5 / 2 = ${5 / 2}'); // 2.5 (double)
  print('5 ~/ 2 = ${5 ~/ 2}'); // 2 (int)
  print('5 % 2 = ${5 % 2}'); // 1 (remainder)

  // Negative numbers
  print('\n--- With Negative Numbers ---');
  print('-10 % 3 = ${-10 % 3}'); // 2 (Dart truncates toward negative infinity)
  print('10 % -3 = ${10 % -3}'); // 1
  print('-10 ~/ 3 = ${-10 ~/ 3}'); // -4

  // Unary minus
  print('\n--- Unary Minus ---');
  int positive = 42;
  int negative = -positive;
  print('positive: $positive');
  print('-positive: $negative');
  print('-(-negative): ${-negative}');

  // Increment and decrement
  print('\n--- Increment (++) ---');
  int count = 0;
  print('Initial count: $count');
  print('++count: ${++count}'); // Pre-increment: increment then return (1)
  print('count after pre-increment: $count'); // 1

  print('count++: ${count++}'); // Post-increment: return then increment (1)
  print('count after post-increment: $count'); // 2

  print('\n--- Decrement (--) ---');
  count = 5;
  print('Initial count: $count');
  print('--count: ${--count}'); // Pre-decrement (4)
  print('count--: ${count--}'); // Post-decrement, returns 4
  print('Final count: $count'); // 3

  // In expressions
  print('\n--- In Expressions ---');
  int x = 5;
  int y = ++x * 2; // x becomes 6, then y = 6 * 2 = 12
  print('x = 5, y = ++x * 2');
  print('x: $x, y: $y');

  x = 5;
  y = x++ * 2; // y = 5 * 2 = 10, then x becomes 6
  print('x = 5, y = x++ * 2');
  print('x: $x, y: $y');

  // Floating point arithmetic
  print('\n--- Floating Point ---');
  double d1 = 3.14159;
  double d2 = 2.0;
  print('$d1 + $d2 = ${d1 + d2}');
  print('$d1 * $d2 = ${d1 * d2}');
  print('$d1 % $d2 = ${d1 % d2}'); // Works with doubles too

  // Special values
  print('\n--- Special Values ---');
  print('1.0 / 0.0 = ${1.0 / 0.0}'); // infinity
  print('-1.0 / 0.0 = ${-1.0 / 0.0}'); // -infinity
  print('0.0 / 0.0 = ${0.0 / 0.0}'); // NaN

  print('\n=== End of Arithmetic Operators Demo ===');
}

/// Demonstrates Dart comparison operators
///
/// Features covered:
/// - Equal (==) and not equal (!=)
/// - Relational operators (>, <, >=, <=)
/// - Identity comparison (identical())

void main() {
  print('=== Comparison Operators ===\n');

  // Equality
  print('--- Equality (==, !=) ---');
  int a = 5;
  int b = 5;
  int c = 10;

  print('a = $a, b = $b, c = $c');
  print('a == b: ${a == b}'); // true
  print('a == c: ${a == c}'); // false
  print('a != c: ${a != c}'); // true
  print('a != b: ${a != b}'); // false

  // String equality
  print('\n--- String Equality ---');
  String s1 = 'hello';
  String s2 = 'hello';
  String s3 = 'Hello';
  print("'hello' == 'hello': ${s1 == s2}"); // true (value comparison)
  print("'hello' == 'Hello': ${s1 == s3}"); // false (case-sensitive)

  // Relational operators
  print('\n--- Relational Operators ---');
  print('5 > 3: ${5 > 3}'); // true
  print('5 < 3: ${5 < 3}'); // false
  print('5 >= 5: ${5 >= 5}'); // true
  print('5 <= 4: ${5 <= 4}'); // false

  // Comparing doubles
  print('\n--- Comparing Doubles ---');
  double d1 = 3.14;
  double d2 = 3.14;
  double d3 = 3.15;
  print('$d1 == $d2: ${d1 == d2}'); // true
  print('$d1 < $d3: ${d1 < d3}'); // true

  // Beware of floating point precision
  print('\n--- Floating Point Precision ---');
  double result = 0.1 + 0.2;
  print('0.1 + 0.2 = $result');
  print('0.1 + 0.2 == 0.3: ${result == 0.3}'); // false!
  // Better approach:
  bool closeEnough = (result - 0.3).abs() < 0.0001;
  print('Close enough to 0.3: $closeEnough');

  // Identity comparison
  print('\n--- Identity (identical()) ---');
  var list1 = [1, 2, 3];
  var list2 = [1, 2, 3];
  var list3 = list1;

  print('list1: $list1');
  print('list2: $list2');
  print('list3 = list1');

  print('\nlist1 == list2: ${list1 == list2}'); // false (different objects)
  print('identical(list1, list2): ${identical(list1, list2)}'); // false
  print('identical(list1, list3): ${identical(list1, list3)}'); // true (same reference)

  // Identity with const
  print('\n--- Identity with const ---');
  const constList1 = [1, 2, 3];
  const constList2 = [1, 2, 3];
  print('identical(constList1, constList2): ${identical(constList1, constList2)}'); // true!

  // Comparing with null
  print('\n--- Comparing with null ---');
  String? nullableStr = getString(null);
  print('nullableStr == null: ${nullableStr == null}'); // true
  nullableStr = getString('value');
  print('nullableStr == null: ${nullableStr == null}'); // false

  // Custom equality (using Object.== override)
  print('\n--- Custom Equality ---');
  var p1 = Point(1, 2);
  var p2 = Point(1, 2);
  var p3 = p1;
  print('p1: $p1, p2: $p2');
  print('p1 == p2: ${p1 == p2}'); // true (custom == implemented)
  print('identical(p1, p2): ${identical(p1, p2)}'); // false
  print('identical(p1, p3): ${identical(p1, p3)}'); // true

  print('\n=== End of Comparison Operators Demo ===');
}

