/// Demonstrates Dart generators (sync* and async*)
///
/// Features covered:
/// - Synchronous generators (sync*)
/// - Asynchronous generators (async*)
/// - yield and yield*

void main() async {
  print('=== Generators ===');
  print('');

  // Synchronous generator (sync*)
  print('--- Synchronous Generator (sync*) ---');

  // Basic sync generator
  print('countTo(5):');
  for (var n in countTo(5)) {
    print('  $n');
  }

  // Generator with early exit
  print('');
  print('First 3 from countTo(10):');
  var count = 0;
  for (var n in countTo(10)) {
    print('  $n');
    if (++count >= 3) break;
  }

  // Range generator
  print('');
  print('range(1, 5):');
  print('  ${range(1, 5).toList()}');

  // Infinite generator (lazy evaluation)
  print('');
  print('First 5 natural numbers:');
  var naturals = naturalNumbers();
  print('  ${naturals.take(5).toList()}');

  // Fibonacci generator
  print('');
  print('First 10 Fibonacci numbers:');
  print('  ${fibonacci().take(10).toList()}');

  // yield* - delegating to another iterable
  print('');
  print('--- yield* (Delegation) ---');
  print('nestedRanges():');
  print('  ${nestedRanges().toList()}');

  // Flatten with yield*
  print('');
  print('flatten([[1,2], [3,4], [5,6]]):');
  var nested = [
    [1, 2],
    [3, 4],
    [5, 6]
  ];
  print('  ${flatten(nested).toList()}');

  // Tree traversal with yield*
  print('');
  print('--- Tree Traversal ---');
  var tree = TreeNode(
    1,
    [
      TreeNode(2, [TreeNode(4), TreeNode(5)]),
      TreeNode(3, [TreeNode(6), TreeNode(7)]),
    ],
  );
  print('Tree values (pre-order):');
  print('  ${traverseTree(tree).toList()}');

  // Asynchronous generator (async*)
  print('');
  print('--- Asynchronous Generator (async*) ---');

  // Basic async generator
  print('countAsyncTo(3):');
  await for (var n in countAsyncTo(3)) {
    print('  $n');
  }

  // Async generator with delay
  print('');
  print('timedEvents():');
  await for (var event in timedEvents().take(3)) {
    print('  $event');
  }

  // Async yield*
  print('');
  print('combinedAsyncStreams():');
  await for (var value in combinedAsyncStreams()) {
    print('  $value');
  }

  // Practical: Paginated data fetching
  print('');
  print('--- Practical: Paginated Fetching ---');
  print('Fetching pages:');
  await for (var item in fetchAllPages()) {
    print('  $item');
  }

  // Generator with filtering
  print('');
  print('--- Generator with Filtering ---');
  print('Primes up to 30:');
  print('  ${primesUpTo(30).toList()}');

  print('');
  print('=== End of Generators Demo ===');
}

// Basic sync generator
Iterable<int> countTo(int max) sync* {
  for (int i = 1; i <= max; i++) {
    yield i;
  }
}

// Range generator
Iterable<int> range(int start, int end, [int step = 1]) sync* {
  for (int i = start; i <= end; i += step) {
    yield i;
  }
}

// Infinite generator
Iterable<int> naturalNumbers() sync* {
  int n = 1;
  while (true) {
    yield n++;
  }
}

// Fibonacci generator
Iterable<int> fibonacci() sync* {
  int a = 0, b = 1;
  while (true) {
    yield a;
    var next = a + b;
    a = b;
    b = next;
  }
}

// yield* - delegate to another iterable
Iterable<int> nestedRanges() sync* {
  yield* range(1, 3);
  yield 100;
  yield* range(4, 6);
}

// Flatten nested iterables
Iterable<T> flatten<T>(Iterable<Iterable<T>> nested) sync* {
  for (var inner in nested) {
    yield* inner;
  }
}

// Tree node for traversal example
class TreeNode<T> {
  final T value;
  final List<TreeNode<T>> children;

  TreeNode(this.value, [this.children = const []]);
}

// Pre-order tree traversal with yield*
Iterable<T> traverseTree<T>(TreeNode<T> node) sync* {
  yield node.value;
  for (var child in node.children) {
    yield* traverseTree(child);
  }
}

// Basic async generator
Stream<int> countAsyncTo(int max) async* {
  for (int i = 1; i <= max; i++) {
    await Future.delayed(Duration(milliseconds: 100));
    yield i;
  }
}

// Async generator with timed events
Stream<String> timedEvents() async* {
  var eventNum = 1;
  while (true) {
    await Future.delayed(Duration(milliseconds: 200));
    yield 'Event ${eventNum++} at ${DateTime.now().second}s';
  }
}

// Async yield* - combine streams
Stream<String> combinedAsyncStreams() async* {
  yield* Stream.fromIterable(['a', 'b', 'c']);
  yield 'middle';
  yield* Stream.fromIterable(['x', 'y', 'z']);
}

// Practical: Paginated API fetching
Stream<String> fetchAllPages() async* {
  int page = 1;
  int maxPages = 3;

  while (page <= maxPages) {
    // Simulate API call
    await Future.delayed(Duration(milliseconds: 100));
    var items = ['Page $page - Item 1', 'Page $page - Item 2'];
    for (var item in items) {
      yield item;
    }
    page++;
  }
}

// Generator with filtering - primes
Iterable<int> primesUpTo(int max) sync* {
  outer:
  for (int n = 2; n <= max; n++) {
    for (int i = 2; i * i <= n; i++) {
      if (n % i == 0) continue outer;
    }
    yield n;
  }
}

/// Demonstrates Dart anonymous functions and closures
///
/// Features covered:
/// - Anonymous functions (lambdas)
/// - Closures
/// - Lexical scope

void main() {
  print('=== Anonymous Functions and Closures ===');
  print('');

  // Anonymous function (lambda)
  print('--- Anonymous Functions ---');

  // Full syntax
  var add = (int a, int b) {
    return a + b;
  };
  print('add(5, 3) = ${add(5, 3)}');

  // Arrow syntax
  var multiply = (int a, int b) => a * b;
  print('multiply(4, 7) = ${multiply(4, 7)}');

  // Without type annotations (inferred)
  var greet = (name) => 'Hello, $name!';
  print('greet("Alice") = ${greet('Alice')}');

  // Passing anonymous functions to methods
  print('');
  print('--- Passing to Methods ---');
  var numbers = [1, 2, 3, 4, 5];

  // Using anonymous function with map
  var doubled = numbers.map((n) => n * 2);
  print('numbers.map((n) => n * 2): ${doubled.toList()}');

  // Using anonymous function with where
  var evens = numbers.where((n) => n % 2 == 0);
  print('numbers.where((n) => n % 2 == 0): ${evens.toList()}');

  // Using anonymous function with reduce
  var sum = numbers.reduce((a, b) => a + b);
  print('numbers.reduce((a, b) => a + b): $sum');

  // Multi-line anonymous function
  print('');
  var processItem = (String item) {
    var trimmed = item.trim();
    var upper = trimmed.toUpperCase();
    return '[$upper]';
  };
  print('processItem("  hello  ") = ${processItem('  hello  ')}');

  // Closures
  print('');
  print('--- Closures ---');

  // Closure capturing outer variable
  int multiplier = 10;
  var multiplyBy = (int n) => n * multiplier;
  print('multiplyBy(5) with multiplier=10: ${multiplyBy(5)}');

  multiplier = 3;
  print('multiplyBy(5) with multiplier=3: ${multiplyBy(5)}');

  // Closure factory
  print('');
  print('--- Closure Factory ---');
  var addFive = makeAdder(5);
  var addTen = makeAdder(10);
  print('addFive(3) = ${addFive(3)}');
  print('addTen(3) = ${addTen(3)}');

  // Counter closure
  print('');
  print('--- Counter Closure ---');
  var counter = makeCounter();
  print('counter() = ${counter()}');
  print('counter() = ${counter()}');
  print('counter() = ${counter()}');

  // Each counter is independent
  var counter2 = makeCounter();
  print('counter2() = ${counter2()}');
  print('counter() = ${counter()}');

  // Lexical scope
  print('');
  print('--- Lexical Scope ---');
  String outerVar = 'outer';

  void innerFunction() {
    String innerVar = 'inner';
    print('  Inside inner: outerVar = $outerVar');
    print('  Inside inner: innerVar = $innerVar');
  }

  innerFunction();
  print('Outside inner: outerVar = $outerVar');
  // print(innerVar); // Error: innerVar not in scope

  // Nested closures
  print('');
  print('--- Nested Closures ---');
  String level1 = 'Level 1';

  var outer = () {
    String level2 = 'Level 2';

    return () {
      String level3 = 'Level 3';
      return '$level1 -> $level2 -> $level3';
    };
  };

  var inner = outer();
  print('Nested result: ${inner()}');

  // Practical example: Event handlers
  print('');
  print('--- Practical: Event Handlers ---');
  var button = Button('Submit');
  button.onClick = () {
    print('  Button "${button.label}" was clicked!');
  };
  button.click();

  // Practical example: Deferred execution
  print('');
  print('--- Practical: Deferred Execution ---');
  var actions = <void Function()>[];

  for (int i = 0; i < 3; i++) {
    actions.add(() => print('  Action $i'));
  }

  print('Executing actions:');
  for (var action in actions) {
    action();
  }

  // Closure in callbacks
  print('');
  print('--- Practical: Callbacks with Context ---');
  processItems(
    ['apple', 'banana', 'cherry'],
    onItem: (item, index) => print('  $index: ${item.toUpperCase()}'),
    onComplete: () => print('  Processing complete!'),
  );

  print('');
  print('=== End of Anonymous Functions and Closures Demo ===');
}

// Closure factory - returns function that captures addBy
int Function(int) makeAdder(int addBy) {
  return (int i) => i + addBy;
}

// Counter factory - each call creates independent counter
int Function() makeCounter() {
  int count = 0;
  return () => ++count;
}

// Button class for event handler example
class Button {
  String label;
  void Function()? onClick;

  Button(this.label);

  void click() {
    onClick?.call();
  }
}

// Callback-based processing
void processItems(
  List<String> items, {
  required void Function(String, int) onItem,
  required void Function() onComplete,
}) {
  for (int i = 0; i < items.length; i++) {
    onItem(items[i], i);
  }
  onComplete();
}

/// Demonstrates Dart conditional statements
///
/// Features covered:
/// - if statement
/// - if-else
/// - if-else if-else chain
/// - if-case (pattern matching)

// Helper functions to prevent compile-time optimization
bool getBool(bool b) => b;
String? getString(String? s) => s;

void main() {
  print('=== Conditional Statements ===');
  print('');

  // Basic if
  print('--- Basic if ---');
  int score = 85;
  if (score >= 60) {
    print('Score $score: Passed!');
  }

  score = 45;
  if (score >= 60) {
