/// Demonstrates Dart assertions and collection control flow
///
/// Features covered:
/// - assert statements
/// - Collection if
/// - Collection for

// Helper functions to prevent compile-time optimization
bool getBool(bool b) => b;
String? getString(String? s) => s;
int? getInt(int? i) => i;

void main() {
  print('=== Assertions and Collection Control Flow ===');
  print('');

  // Assertions (only run in debug mode)
  print('--- Assertions ---');
  print('Note: Assertions only run in debug mode (dart run --enable-asserts)');
  print('');

  int age = 25;
  assert(age >= 0, 'Age cannot be negative');
  print('Age $age passed assertion (age >= 0)');

  String name = 'Alice';
  assert(name.isNotEmpty, 'Name cannot be empty');
  print('Name "$name" passed assertion (not empty)');

  // Multiple assertions for validation
  void validateUser(String username, int age, String email) {
    assert(username.isNotEmpty, 'Username required');
    assert(age >= 0 && age <= 150, 'Age must be 0-150');
    assert(email.contains('@'), 'Invalid email format');
    print('User validated: $username, $age, $email');
  }

  validateUser('bob', 30, 'bob@example.com');

  // Assert with expression message
  int value = 42;
  assert(value > 0, 'Value must be positive, got: $value');
  print('Value $value passed assertion');

  // Collection if
  print('');
  print('--- Collection if ---');

  bool includeAdmin = getBool(true);
  var users = [
    'user1',
    'user2',
    if (includeAdmin) 'admin',
  ];
  print('Users (with admin): $users');

  includeAdmin = getBool(false);
  users = [
    'user1',
    'user2',
    if (includeAdmin) 'admin',
  ];
  print('Users (without admin): $users');

  // Collection if-else
  print('');
  bool isProduction = getBool(false);
  var config = {
    'name': 'MyApp',
    if (isProduction) 'apiUrl': 'https://api.example.com' else 'apiUrl': 'http://localhost:3000',
    if (isProduction) 'debug': false else 'debug': true,
  };
  print('Config (dev): $config');

  isProduction = getBool(true);
  config = {
    'name': 'MyApp',
    if (isProduction) 'apiUrl': 'https://api.example.com' else 'apiUrl': 'http://localhost:3000',
    if (isProduction) 'debug': false else 'debug': true,
  };
  print('Config (prod): $config');

  // Nested collection if
  print('');
  bool hasPermission = getBool(true);
  bool isOwner = getBool(false);
  var menuItems = [
    'View',
    if (hasPermission) ...[
      'Edit',
      if (isOwner) 'Delete',
    ],
    'Help',
  ];
  print('Menu (permission, not owner): $menuItems');

  isOwner = getBool(true);
  menuItems = [
    'View',
    if (hasPermission) ...[
      'Edit',
      if (isOwner) 'Delete',
    ],
    'Help',
  ];
  print('Menu (permission, owner): $menuItems');

  // Collection for
  print('');
  print('--- Collection for ---');

  var squares = [for (int i = 1; i <= 5; i++) i * i];
  print('Squares: $squares');

  var doubled = [for (var n in [1, 2, 3, 4, 5]) n * 2];
  print('Doubled: $doubled');

  // Collection for with if
  var evenNumbers = [for (int i = 1; i <= 10; i++) if (i % 2 == 0) i];
  print('Even numbers 1-10: $evenNumbers');

  // Nested collection for
  var pairs = [
    for (int i = 1; i <= 3; i++)
      for (int j = i + 1; j <= 3; j++) '($i, $j)',
  ];
  print('Pairs: $pairs');

  // Collection for with transformation
  var names = ['alice', 'bob', 'charlie'];
  var capitalized = [for (var name in names) name[0].toUpperCase() + name.substring(1)];
  print('Capitalized: $capitalized');

  // Building a Map with collection for
  print('');
  print('--- Building Maps ---');
  var indices = {for (var i = 0; i < names.length; i++) names[i]: i};
  print('Name indices: $indices');

  // Building a Set
  var uniqueChars = {for (var name in names) ...name.split('')};
  print('Unique characters: $uniqueChars');

  // Practical examples
  print('');
  print('--- Practical Examples ---');

  // Building widget children
  var items = ['Item 1', 'Item 2', 'Item 3'];
  bool showHeader = getBool(true);
  bool showFooter = getBool(false);

  var widgets = [
    if (showHeader) 'Header',
    for (var item in items) 'Content: $item',
    if (showFooter) 'Footer',
  ];
  print('Widgets: $widgets');

  // Flattening nested structure
  var nested = [
    [1, 2],
    [3, 4],
    [5, 6]
  ];
  var flat = [for (var list in nested) for (var item in list) item];
  print('Flattened: $flat');

  // Building query parameters
  String? searchTerm = getString(null);
  int? page = getInt(2);
  var queryParams = {
    if (searchTerm != null) 'q': searchTerm,
    if (page != null) 'page': page.toString(),
    'limit': '10',
  };
  print('Query params: $queryParams');

  print('');
  print('=== End of Assertions and Collection Control Flow Demo ===');
}

/// Demonstrates Dart switch statement
///
/// Features covered:
/// - Basic switch statement
/// - Multiple case labels
/// - Default case
/// - break and fall-through prevention
/// - Switch on various types

void main() {
  print('=== Switch Statement ===');
  print('');

  // Basic switch
  print('--- Basic Switch ---');
  String grade = 'B';

  switch (grade) {
    case 'A':
      print('Excellent!');
      break;
    case 'B':
      print('Good job!');
      break;
    case 'C':
      print('Satisfactory');
      break;
    case 'D':
      print('Needs improvement');
      break;
    case 'F':
      print('Failed');
      break;
    default:
      print('Invalid grade');
  }

  // Multiple case labels
  print('');
  print('--- Multiple Case Labels ---');
  int dayNumber = 6;

  switch (dayNumber) {
    case 1:
    case 2:
    case 3:
    case 4:
    case 5:
      print('Day $dayNumber: Weekday');
      break;
    case 6:
    case 7:
      print('Day $dayNumber: Weekend');
      break;
    default:
      print('Invalid day number');
  }

  // Switch with different types
  print('');
  print('--- Switch on Different Types ---');

  // String
  String command = 'start';
  switch (command) {
    case 'start':
      print('Starting...');
      break;
    case 'stop':
      print('Stopping...');
      break;
    case 'pause':
      print('Pausing...');
      break;
  }

  // int with ranges (using when in Dart 3)
  print('');
  print('--- Switch with when Clause ---');
  int score = 85;

  switch (score) {
    case int s when s >= 90:
      print('Score $score: Grade A');
      break;
    case int s when s >= 80:
      print('Score $score: Grade B');
      break;
    case int s when s >= 70:
      print('Score $score: Grade C');
      break;
    case int s when s >= 60:
      print('Score $score: Grade D');
      break;
    default:
      print('Score $score: Grade F');
  }

  // Switch with enum
  print('');
  print('--- Switch on Enum ---');
  Color color = Color.green;

  switch (color) {
    case Color.red:
      print('Stop!');
      break;
    case Color.yellow:
      print('Caution!');
      break;
    case Color.green:
      print('Go!');
      break;
  }

  // Switch with pattern matching (Dart 3)
  print('');
  print('--- Switch with Pattern Matching ---');
  Object value = [1, 2, 3];

  switch (value) {
    case int i:
      print('Integer: $i');
      break;
    case String s:
      print('String: $s');
      break;
    case List<int> list when list.isNotEmpty:
      print('Non-empty int list: $list');
      break;
    case List list:
      print('Some list: $list');
      break;
    default:
      print('Unknown type');
  }

  // Switch with records
  print('');
  print('--- Switch on Records ---');
  var point = (2, 0);

  switch (point) {
    case (0, 0):
      print('Origin');
      break;
    case (var x, 0):
      print('On x-axis at x=$x');
      break;
    case (0, var y):
      print('On y-axis at y=$y');
      break;
    case (var x, var y):
      print('Point at ($x, $y)');
      break;
  }

  // Switch with continue (labeled cases)
  print('');
  print('--- Switch with continue ---');
  String input = 'YES';

  switch (input.toLowerCase()) {
    case 'yes':
    case 'y':
      print('Confirmed');
      continue shared;
    case 'no':
    case 'n':
      print('Declined');
      continue shared;
    shared:
    case 'shared':
      print('Processing complete');
      break;
    default:
      print('Unknown response');
  }

  // Empty switch body handling
  print('');
  print('--- Exhaustive Switch ---');
  Status status = Status.active;

  // When all enum values are handled, no default needed
  switch (status) {
    case Status.pending:
      print('Waiting...');
      break;
    case Status.active:
      print('Running');
      break;
    case Status.completed:
      print('Done!');
      break;
    case Status.failed:
      print('Error occurred');
      break;
  }

  print('');
  print('=== End of Switch Statement Demo ===');
}

